/**
 * Specifies the the layout of the Card action buttons.
 *
 * The possible values are:
 * * (Default) `start`
 * * `center`
 * * `end`
 * * `stretched`
 *
 */
export declare type ActionsLayout = 'start' | 'center' | 'end' | 'stretched';
