import { PreventableEvent } from "@progress/kendo-angular-common";
/**
 * Arguments for the `action` event of the ExpansionPanel.
 */
export class ExpansionPanelActionEvent extends PreventableEvent {
    /**
     * @hidden
     */
    constructor(args) {
        super();
        Object.assign(this, args);
    }
}
