/**
 * Specifies the gaps between the elements of the GridLayout.
 */
export interface GridLayoutGapSettings {
    /**
     * Defines the row gap between the elements.
     */
    rows?: number | string;
    /**
     * Defines the column gap between the elements.
     */
    cols?: number | string;
}
