/**
 * Тhe values which determine the spacing between the layout items horizontally and vertically.
 */
export interface TileLayoutGap {
    /**
     * The vertical spacing. Numeric values are treated as pixels.
     * @default 16
     */
    rows?: number;
    /**
     * The horizontal spacing. Numeric values are treated as pixels.
     * @default 16
     */
    columns?: number;
}
